sourcesContent:
import React, { useState, useEffect } from 'react';
import { 
  Plus, Search, Download, Eye, Edit, Trash2, 
  CheckCircle, XCircle, Clock, MapPin, Calendar, User,
  ClipboardList, Play, RefreshCw,
  AlertCircle, Star, Send, X
} from 'lucide-react';
import api, { endpoints } from '../api';
import { Card, Button, Badge, Modal, Input, Select, Textarea, LoadingSpinner } from '../components/ui';

// Types
interface Project {
  id: number;
  code: string;
  title: string;
  sector: string;
}

interface Beneficiary {
  id: number;
  beneficiary_id: string;
  name: string;
  phone?: string;
  village?: string;
  district?: string;
  state?: string;
  gender?: string;
  age?: number;
  category?: string;
  occupation?: string;
  grant_amount_received?: string;
}

interface QuestionnaireTemplate {
  id: number;
  name: string;
  description?: string;
  sections?: Section[];
}

interface ProjectQuestionnaire {
  id: number;
  project: number;
  template: number;
  template_name: string;
  name: string;
  is_active: boolean;
}

interface Section {
  id: number;
  title: string;
  description?: string;
  order: number;
  questions: Question[];
}

interface Question {
  id: number;
  text: string;
  help_text?: string;
  question_type: string;
  options?: string[];
  validation_rules?: Record<string, any>;
  is_required: boolean;
  order: number;
}

interface Answer {
  id?: number;
  question: number;
  question_text?: string;
  question_type?: string;
  section_title?: string;
  text_value?: string;
  numeric_value?: number;
  date_value?: string;
  time_value?: string;
  selected_options?: string[];
  location_lat?: number;
  location_lng?: number;
  is_skipped?: boolean;
  skip_reason?: string;
  display_value?: string;
}

interface Interview {
  id: number;
  project: number;
  project_code: string;
  project_title?: string;
  questionnaire: number;
  questionnaire_name?: string;
  beneficiary: number;
  beneficiary_name: string;
  beneficiary_id?: string;
  beneficiary_details?: Beneficiary;
  interviewer: number;
  interviewer_name: string;
  interview_date: string;
  interview_start_time?: string;
  interview_end_time?: string;
  interview_location?: string;
  gps_latitude?: number;
  gps_longitude?: number;
  status: 'draft' | 'in_progress' | 'submitted' | 'reviewed' | 'approved' | 'rejected';
  status_display?: string;
  interviewer_notes?: string;
  observations?: string;
  impact_score?: number;
  satisfaction_score?: number;
  reviewed_by?: number;
  reviewed_by_name?: string;
  reviewed_at?: string;
  review_comments?: string;
  duration_minutes?: number;
  answer_count?: number;
  answers?: Answer[];
  created_at: string;
  submitted_at?: string;
}

interface FieldResource {
  id: number;
  username: string;
  first_name: string;
  last_name: string;
  full_name?: string;
  email: string;
  phone_number?: string;
  is_available: boolean;
}

const InterviewsPage: React.FC = () => {
  // State
  const [interviews, setInterviews] = useState<Interview[]>([]);
  const [filteredInterviews, setFilteredInterviews] = useState<Interview[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Filters
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('');
  const [projectFilter, setProjectFilter] = useState('');
  const [dateFilter, setDateFilter] = useState('');

  // Data for dropdowns
  const [projects, setProjects] = useState<Project[]>([]);
  const [beneficiaries, setBeneficiaries] = useState<Beneficiary[]>([]);
  const [questionnaires, setQuestionnaires] = useState<ProjectQuestionnaire[]>([]);
  const [templates, setTemplates] = useState<QuestionnaireTemplate[]>([]);
  const [fieldResources, setFieldResources] = useState<FieldResource[]>([]);

  // Modals
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [showViewModal, setShowViewModal] = useState(false);
  const [showConductModal, setShowConductModal] = useState(false);
  const [showReviewModal, setShowReviewModal] = useState(false);

  // Selected data
  const [selectedInterview, setSelectedInterview] = useState<Interview | null>(null);
  const [selectedTemplate, setSelectedTemplate] = useState<QuestionnaireTemplate | null>(null);

  // Form state
  const [formData, setFormData] = useState({
    project: '',
    questionnaire: '',
    beneficiary: '',
    interviewer: '',
    interview_date: new Date().toISOString().split('T')[0],
    interview_location: '',
    interviewer_notes: '',
  });

  // Interview answers
  const [answers, setAnswers] = useState<Record<number, any>>({});
  const [currentSectionIndex, setCurrentSectionIndex] = useState(0);

  // Review form
  const [reviewData, setReviewData] = useState({
    status: '',
    review_comments: '',
  });

  const [submitting, setSubmitting] = useState(false);

  // Load data
  useEffect(() => {
    loadInterviews();
    loadProjects();
    loadFieldResources();
    loadTemplates();
  }, []);

  // Apply filters
  useEffect(() => {
    let filtered = [...interviews];

    if (searchTerm) {
      const term = searchTerm.toLowerCase();
      filtered = filtered.filter(i => 
        i.beneficiary_name?.toLowerCase().includes(term) ||
        i.project_code?.toLowerCase().includes(term) ||
        i.interviewer_name?.toLowerCase().includes(term) ||
        i.beneficiary_id?.toLowerCase().includes(term)
      );
    }

    if (statusFilter) {
      filtered = filtered.filter(i => i.status === statusFilter);
    }

    if (projectFilter) {
      filtered = filtered.filter(i => i.project.toString() === projectFilter);
    }

    if (dateFilter) {
      const today = new Date();
      const filterDate = new Date(today);
      
      switch (dateFilter) {
        case 'today':
          filtered = filtered.filter(i => i.interview_date === today.toISOString().split('T')[0]);
          break;
        case 'week':
          filterDate.setDate(today.getDate() - 7);
          filtered = filtered.filter(i => new Date(i.interview_date) >= filterDate);
          break;
        case 'month':
          filterDate.setMonth(today.getMonth() - 1);
          filtered = filtered.filter(i => new Date(i.interview_date) >= filterDate);
          break;
      }
    }

    setFilteredInterviews(filtered);
  }, [interviews, searchTerm, statusFilter, projectFilter, dateFilter]);

  const loadInterviews = async () => {
    setLoading(true);
    try {
      const data = await api.get<Interview[]>(endpoints.interviews);
      const interviewList = Array.isArray(data) ? data : (data as any).results || [];
      setInterviews(interviewList);
    } catch (err: any) {
      console.error('Error loading interviews:', err);
      setError('Failed to load interviews');
    }
    setLoading(false);
  };

  const loadProjects = async () => {
    try {
      const data = await api.get<Project[]>(endpoints.projects);
      setProjects(Array.isArray(data) ? data : (data as any).results || []);
    } catch (err) {
      console.error('Error loading projects:', err);
    }
  };

  const loadFieldResources = async () => {
    try {
      const data = await api.get<FieldResource[]>(endpoints.fieldResources);
      setFieldResources(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Error loading field resources:', err);
    }
  };

  const loadTemplates = async () => {
    try {
      const data = await api.get<QuestionnaireTemplate[]>(endpoints.templates);
      setTemplates(Array.isArray(data) ? data : (data as any).results || []);
    } catch (err) {
      console.error('Error loading templates:', err);
    }
  };

  const loadProjectBeneficiaries = async (projectId: string) => {
    try {
      const data = await api.get<Beneficiary[]>(`${endpoints.beneficiaries}?project=${projectId}`);
      setBeneficiaries(Array.isArray(data) ? data : (data as any).results || []);
    } catch (err) {
      console.error('Error loading beneficiaries:', err);
    }
  };

  const loadProjectQuestionnaires = async (projectId: string) => {
    try {
      const data = await api.get<ProjectQuestionnaire[]>(`${endpoints.projectQuestionnaires}?project=${projectId}`);
      setQuestionnaires(Array.isArray(data) ? data : (data as any).results || []);
    } catch (err) {
      console.error('Error loading questionnaires:', err);
    }
  };

  const loadInterviewDetails = async (interviewId: number) => {
    try {
      const data = await api.get<Interview>(`${endpoints.interviews}${interviewId}/`);
      setSelectedInterview(data);
      return data;
    } catch (err) {
      console.error('Error loading interview details:', err);
      return null;
    }
  };

  const loadTemplateDetails = async (templateId: number) => {
    try {
      const data = await api.get<QuestionnaireTemplate>(`${endpoints.templates}${templateId}/`);
      setSelectedTemplate(data);
      return data;
    } catch (err) {
      console.error('Error loading template:', err);
      return null;
    }
  };

  // Handlers
  const handleProjectChange = (projectId: string) => {
    setFormData(prev => ({ ...prev, project: projectId, questionnaire: '', beneficiary: '' }));
    if (projectId) {
      loadProjectBeneficiaries(projectId);
      loadProjectQuestionnaires(projectId);
    } else {
      setBeneficiaries([]);
      setQuestionnaires([]);
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleCreateInterview = async () => {
    if (!formData.project || !formData.beneficiary || !formData.interview_date) {
      setError('Please fill in all required fields');
      return;
    }

    setSubmitting(true);
    try {
      const payload: any = {
        project: parseInt(formData.project),
        beneficiary: parseInt(formData.beneficiary),
        interview_date: formData.interview_date,
        interview_location: formData.interview_location,
        interviewer_notes: formData.interviewer_notes,
        status: 'draft',
      };

      if (formData.questionnaire) {
        payload.questionnaire = parseInt(formData.questionnaire);
      }
      if (formData.interviewer) {
        payload.interviewer = parseInt(formData.interviewer);
      }

      const newInterview = await api.post<Interview>(endpoints.interviews, payload);
      
      setShowCreateModal(false);
      resetForm();
      loadInterviews();

      // Open conduct modal for the new interview
      if (newInterview.id) {
        handleConductInterview(newInterview);
      }
    } catch (err: any) {
      const errorMessage = err.response?.data?.detail 
        || Object.values(err.response?.data || {}).flat().join(', ')
        || err.message 
        || 'Failed to create interview';
      setError(errorMessage);
    }
    setSubmitting(false);
  };

  const handleConductInterview = async (interview: Interview) => {
    setSelectedInterview(interview);
    setCurrentSectionIndex(0);
    setAnswers({});

    // Load full interview details
    const details = await loadInterviewDetails(interview.id);
    if (details?.answers) {
      const existingAnswers: Record<number, any> = {};
      details.answers.forEach(a => {
        existingAnswers[a.question] = {
          text_value: a.text_value,
          numeric_value: a.numeric_value,
          date_value: a.date_value,
          selected_options: a.selected_options || [],
          location_lat: a.location_lat,
          location_lng: a.location_lng,
          is_skipped: a.is_skipped,
          skip_reason: a.skip_reason,
        };
      });
      setAnswers(existingAnswers);
    }

    // Load a template (first available for now)
    if (templates.length > 0) {
      await loadTemplateDetails(templates[0].id);
    }

    setShowConductModal(true);
  };

  const handleViewInterview = async (interview: Interview) => {
    await loadInterviewDetails(interview.id);
    setShowViewModal(true);
  };

  const handleReviewInterview = async (interview: Interview) => {
    await loadInterviewDetails(interview.id);
    setReviewData({ status: '', review_comments: '' });
    setShowReviewModal(true);
  };

  const handleAnswerChange = (questionId: number, field: string, value: any) => {
    setAnswers(prev => ({
      ...prev,
      [questionId]: {
        ...prev[questionId],
        [field]: value,
      }
    }));
  };

  const handleOptionToggle = (questionId: number, option: string, isMultiple: boolean) => {
    setAnswers(prev => {
      const current = prev[questionId]?.selected_options || [];
      let newOptions: string[];
      
      if (isMultiple) {
        newOptions = current.includes(option) 
          ? current.filter((o: string) => o !== option)
          : [...current, option];
      } else {
        newOptions = [option];
      }
      
      return {
        ...prev,
        [questionId]: {
          ...prev[questionId],
          selected_options: newOptions,
        }
      };
    });
  };

  const handleSaveAnswers = async (status: 'draft' | 'in_progress' | 'submitted' = 'in_progress') => {
    if (!selectedInterview) return;

    setSubmitting(true);
    try {
      // Prepare answers for bulk save
      const answersPayload = Object.entries(answers).map(([questionId, answer]) => ({
        question: parseInt(questionId),
        ...answer,
      }));

      // Save answers
      if (answersPayload.length > 0) {
        await api.post(endpoints.answers, {
          interview_id: selectedInterview.id,
          answers: answersPayload,
        });
      }

      // Update interview status
      await api.patch(`${endpoints.interviews}${selectedInterview.id}/`, { status });

      setShowConductModal(false);
      loadInterviews();
    } catch (err: any) {
      const errorMessage = err.response?.data?.detail || 'Failed to save answers';
      setError(errorMessage);
    }
    setSubmitting(false);
  };

  const handleSubmitReview = async () => {
    if (!selectedInterview || !reviewData.status) return;

    setSubmitting(true);
    try {
      await api.patch(`${endpoints.interviews}${selectedInterview.id}/`, {
        status: reviewData.status,
        review_comments: reviewData.review_comments,
      });

      setShowReviewModal(false);
      loadInterviews();
    } catch (err: any) {
      setError('Failed to submit review');
    }
    setSubmitting(false);
  };

  const handleDeleteInterview = async (interview: Interview) => {
    if (!window.confirm(`Are you sure you want to delete interview with ${interview.beneficiary_name}?`)) {
      return;
    }

    try {
      await api.delete(`${endpoints.interviews}${interview.id}/`);
      loadInterviews();
    } catch (err) {
      setError('Failed to delete interview');
    }
  };

  const resetForm = () => {
    setFormData({
      project: '',
      questionnaire: '',
      beneficiary: '',
      interviewer: '',
      interview_date: new Date().toISOString().split('T')[0],
      interview_location: '',
      interviewer_notes: '',
    });
    setBeneficiaries([]);
    setQuestionnaires([]);
    setError('');
  };

  const getStatusBadge = (status: string) => {
    const config: Record<string, { variant: 'default' | 'success' | 'warning' | 'danger' | 'info'; icon: React.ReactNode }> = {
      draft: { variant: 'default', icon: <Edit className="w-3 h-3" /> },
      in_progress: { variant: 'warning', icon: <Clock className="w-3 h-3" /> },
      submitted: { variant: 'info', icon: <Send className="w-3 h-3" /> },
      reviewed: { variant: 'info', icon: <Eye className="w-3 h-3" /> },
      approved: { variant: 'success', icon: <CheckCircle className="w-3 h-3" /> },
      rejected: { variant: 'danger', icon: <XCircle className="w-3 h-3" /> },
    };
    const { variant, icon } = config[status] || { variant: 'default', icon: null };
    return (
      <Badge variant={variant}>
        <span className="flex items-center gap-1">
          {icon}
          {status.replace('_', ' ')}
        </span>
      </Badge>
    );
  };

  const getScoreColor = (score: number) => {
    if (score >= 4) return 'text-emerald-600';
    if (score >= 3) return 'text-amber-600';
    return 'text-red-600';
  };

  // Statistics
  const stats = {
    total: interviews.length,
    draft: interviews.filter(i => i.status === 'draft').length,
    inProgress: interviews.filter(i => i.status === 'in_progress').length,
    submitted: interviews.filter(i => i.status === 'submitted').length,
    approved: interviews.filter(i => i.status === 'approved').length,
    rejected: interviews.filter(i => i.status === 'rejected').length,
    avgScore: interviews.filter(i => i.impact_score).length > 0
      ? (interviews.reduce((acc, i) => acc + (i.impact_score || 0), 0) / 
         interviews.filter(i => i.impact_score).length).toFixed(2)
      : 'N/A',
  };

  // Export to CSV
  const handleExport = () => {
    const headers = ['Beneficiary', 'Project', 'Interviewer', 'Date', 'Status', 'Score', 'Duration'];
    const rows = filteredInterviews.map(i => [
      i.beneficiary_name,
      i.project_code,
      i.interviewer_name,
      i.interview_date,
      i.status,
      i.impact_score || '-',
      i.duration_minutes ? `${i.duration_minutes} min` : '-',
    ]);

    const csv = [headers, ...rows].map(row => row.join(',')).join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `interviews_${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
  };

  // Render question input based on type
  const renderQuestionInput = (question: Question) => {
    const answer = answers[question.id] || {};
    const questionType = question.question_type;

    switch (questionType) {
      case 'text':
        return (
          <Input
            value={answer.text_value || ''}
            onChange={(e) => handleAnswerChange(question.id, 'text_value', e.target.value)}
            placeholder="Enter your answer"
          />
        );

      case 'textarea':
        return (
          <Textarea
            value={answer.text_value || ''}
            onChange={(e) => handleAnswerChange(question.id, 'text_value', e.target.value)}
            placeholder="Enter your detailed answer"
            rows={4}
          />
        );

      case 'number':
      case 'decimal':
        return (
          <Input
            type="number"
            value={answer.numeric_value || ''}
            onChange={(e) => handleAnswerChange(question.id, 'numeric_value', parseFloat(e.target.value))}
            placeholder="Enter a number"
          />
        );

      case 'date':
        return (
          <Input
            type="date"
            value={answer.date_value || ''}
            onChange={(e) => handleAnswerChange(question.id, 'date_value', e.target.value)}
          />
        );

      case 'time':
        return (
          <Input
            type="time"
            value={answer.time_value || ''}
            onChange={(e) => handleAnswerChange(question.id, 'time_value', e.target.value)}
          />
        );

      case 'single_choice':
      case 'dropdown':
        return (
          <div className="space-y-2">
            {(question.options || []).map((option, idx) => (
              <label key={idx} className="flex items-center gap-3 p-3 border rounded-lg cursor-pointer hover:bg-gray-50">
                <input
                  type="radio"
                  name={`question_${question.id}`}
                  checked={(answer.selected_options || [])[0] === option}
                  onChange={() => handleOptionToggle(question.id, option, false)}
                  className="w-4 h-4 text-emerald-600"
                />
                <span>{option}</span>
              </label>
            ))}
          </div>
        );

      case 'multiple_choice':
        return (
          <div className="space-y-2">
            {(question.options || []).map((option, idx) => (
              <label key={idx} className="flex items-center gap-3 p-3 border rounded-lg cursor-pointer hover:bg-gray-50">
                <input
                  type="checkbox"
                  checked={(answer.selected_options || []).includes(option)}
                  onChange={() => handleOptionToggle(question.id, option, true)}
                  className="w-4 h-4 text-emerald-600 rounded"
                />
                <span>{option}</span>
              </label>
            ))}
          </div>
        );

      case 'rating':
        return (
          <div className="flex gap-2">
            {[1, 2, 3, 4, 5].map((rating) => (
              <button
                key={rating}
                type="button"
                onClick={() => handleAnswerChange(question.id, 'numeric_value', rating)}
                className={`w-12 h-12 rounded-lg border-2 flex items-center justify-center transition-all ${
                  answer.numeric_value === rating
                    ? 'bg-emerald-600 border-emerald-600 text-white'
                    : 'border-gray-300 hover:border-emerald-400'
                }`}
              >
                <Star className={`w-5 h-5 ${answer.numeric_value === rating ? 'fill-current' : ''}`} />
              </button>
            ))}
          </div>
        );

      case 'yes_no':
        return (
          <div className="flex gap-4">
            <button
              type="button"
              onClick={() => handleAnswerChange(question.id, 'selected_options', ['Yes'])}
              className={`flex-1 py-3 px-6 rounded-lg border-2 font-medium transition-all ${
                (answer.selected_options || [])[0] === 'Yes'
                  ? 'bg-emerald-600 border-emerald-600 text-white'
                  : 'border-gray-300 hover:border-emerald-400'
              }`}
            >
              <CheckCircle className="w-5 h-5 inline mr-2" />
              Yes
            </button>
            <button
              type="button"
              onClick={() => handleAnswerChange(question.id, 'selected_options', ['No'])}
              className={`flex-1 py-3 px-6 rounded-lg border-2 font-medium transition-all ${
                (answer.selected_options || [])[0] === 'No'
                  ? 'bg-red-600 border-red-600 text-white'
                  : 'border-gray-300 hover:border-red-400'
              }`}
            >
              <XCircle className="w-5 h-5 inline mr-2" />
              No
            </button>
          </div>
        );

      case 'yes_no_na':
        return (
          <div className="flex gap-3">
            {['Yes', 'No', 'N/A'].map((option) => (
              <button
                key={option}
                type="button"
                onClick={() => handleAnswerChange(question.id, 'selected_options', [option])}
                className={`flex-1 py-3 px-4 rounded-lg border-2 font-medium transition-all ${
                  (answer.selected_options || [])[0] === option
                    ? option === 'Yes' ? 'bg-emerald-600 border-emerald-600 text-white'
                    : option === 'No' ? 'bg-red-600 border-red-600 text-white'
                    : 'bg-gray-600 border-gray-600 text-white'
                    : 'border-gray-300 hover:border-gray-400'
                }`}
              >
                {option}
              </button>
            ))}
          </div>
        );

      case 'location':
        return (
          <div className="space-y-2">
            <div className="grid grid-cols-2 gap-4">
              <Input
                label="Latitude"
                type="number"
                step="0.000001"
                value={answer.location_lat || ''}
                onChange={(e) => handleAnswerChange(question.id, 'location_lat', parseFloat(e.target.value))}
                placeholder="e.g., 28.6139"
              />
              <Input
                label="Longitude"
                type="number"
                step="0.000001"
                value={answer.location_lng || ''}
                onChange={(e) => handleAnswerChange(question.id, 'location_lng', parseFloat(e.target.value))}
                placeholder="e.g., 77.2090"
              />
            </div>
            <Button
              variant="secondary"
              onClick={() => {
                if (navigator.geolocation) {
                  navigator.geolocation.getCurrentPosition((pos) => {
                    handleAnswerChange(question.id, 'location_lat', pos.coords.latitude);
                    handleAnswerChange(question.id, 'location_lng', pos.coords.longitude);
                  });
                }
              }}
            >
              <MapPin className="w-4 h-4" /> Get Current Location
            </Button>
          </div>
        );

      default:
        return (
          <Input
            value={answer.text_value || ''}
            onChange={(e) => handleAnswerChange(question.id, 'text_value', e.target.value)}
            placeholder="Enter your answer"
          />
        );
    }
  };

  // Calculate section progress
  const getSectionProgress = (section: Section) => {
    const totalQuestions = section.questions.length;
    const answeredQuestions = section.questions.filter(q => {
      const answer = answers[q.id];
      if (!answer) return false;
      return answer.text_value || answer.numeric_value || answer.date_value || 
             (answer.selected_options && answer.selected_options.length > 0) ||
             answer.location_lat || answer.is_skipped;
    }).length;
    return { answered: answeredQuestions, total: totalQuestions };
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Interviews</h1>
          <p className="text-gray-500 mt-1">Manage and conduct beneficiary interviews</p>
        </div>
        <div className="flex gap-3">
          <Button variant="secondary" onClick={handleExport}>
            <Download className="w-4 h-4" /> Export
          </Button>
          <Button onClick={() => { resetForm(); setShowCreateModal(true); }}>
            <Plus className="w-4 h-4" /> New Interview
          </Button>
        </div>
      </div>

      {/* Error Display */}
      {error && (
        <div className="bg-red-50 text-red-600 p-4 rounded-lg flex items-center gap-2">
          <AlertCircle className="w-5 h-5" />
          {error}
          <button onClick={() => setError('')} className="ml-auto">
            <X className="w-4 h-4" />
          </button>
        </div>
      )}

      {/* Statistics */}
      <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-7 gap-4">
        <Card className="p-4 text-center cursor-pointer hover:shadow-md transition-shadow" onClick={() => setStatusFilter('')}>
          <p className="text-xs text-gray-500 uppercase tracking-wide">Total</p>
          <p className="text-2xl font-bold text-gray-900 mt-1">{stats.total}</p>
        </Card>
        <Card className="p-4 text-center cursor-pointer hover:shadow-md transition-shadow" onClick={() => setStatusFilter('draft')}>
          <p className="text-xs text-gray-500 uppercase tracking-wide">Draft</p>
          <p className="text-2xl font-bold text-gray-600 mt-1">{stats.draft}</p>
        </Card>
        <Card className="p-4 text-center cursor-pointer hover:shadow-md transition-shadow" onClick={() => setStatusFilter('in_progress')}>
          <p className="text-xs text-gray-500 uppercase tracking-wide">In Progress</p>
          <p className="text-2xl font-bold text-amber-600 mt-1">{stats.inProgress}</p>
        </Card>
        <Card className="p-4 text-center cursor-pointer hover:shadow-md transition-shadow" onClick={() => setStatusFilter('submitted')}>
          <p className="text-xs text-gray-500 uppercase tracking-wide">Submitted</p>
          <p className="text-2xl font-bold text-blue-600 mt-1">{stats.submitted}</p>
        </Card>
        <Card className="p-4 text-center cursor-pointer hover:shadow-md transition-shadow" onClick={() => setStatusFilter('approved')}>
          <p className="text-xs text-gray-500 uppercase tracking-wide">Approved</p>
          <p className="text-2xl font-bold text-emerald-600 mt-1">{stats.approved}</p>
        </Card>
        <Card className="p-4 text-center cursor-pointer hover:shadow-md transition-shadow" onClick={() => setStatusFilter('rejected')}>
          <p className="text-xs text-gray-500 uppercase tracking-wide">Rejected</p>
          <p className="text-2xl font-bold text-red-600 mt-1">{stats.rejected}</p>
        </Card>
        <Card className="p-4 text-center">
          <p className="text-xs text-gray-500 uppercase tracking-wide">Avg Score</p>
          <p className="text-2xl font-bold text-purple-600 mt-1">{stats.avgScore}</p>
        </Card>
      </div>

      {/* Filters */}
      <Card className="p-4">
        <div className="flex flex-wrap items-center gap-4">
          <div className="flex-1 min-w-64">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400" />
              <input
                type="text"
                placeholder="Search by beneficiary, project, or interviewer..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500 focus:border-emerald-500"
              />
            </div>
          </div>
          
          <select
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500"
          >
            <option value="">All Status</option>
            <option value="draft">Draft</option>
            <option value="in_progress">In Progress</option>
            <option value="submitted">Submitted</option>
            <option value="approved">Approved</option>
            <option value="rejected">Rejected</option>
          </select>

          <select
            value={projectFilter}
            onChange={(e) => setProjectFilter(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500"
          >
            <option value="">All Projects</option>
            {projects.map(p => (
              <option key={p.id} value={p.id}>{p.code} - {p.title}</option>
            ))}
          </select>

          <select
            value={dateFilter}
            onChange={(e) => setDateFilter(e.target.value)}
            className="px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500"
          >
            <option value="">All Time</option>
            <option value="today">Today</option>
            <option value="week">Last 7 Days</option>
            <option value="month">Last 30 Days</option>
          </select>

          {(searchTerm || statusFilter || projectFilter || dateFilter) && (
            <Button variant="secondary" onClick={() => {
              setSearchTerm('');
              setStatusFilter('');
              setProjectFilter('');
              setDateFilter('');
            }}>
              <RefreshCw className="w-4 h-4" /> Clear
            </Button>
          )}
        </div>
      </Card>

      {/* Interviews Table */}
      <Card className="overflow-hidden">
        {loading ? (
          <div className="flex items-center justify-center py-12">
            <LoadingSpinner size="lg" />
          </div>
        ) : filteredInterviews.length === 0 ? (
          <div className="text-center py-12">
            <ClipboardList className="w-16 h-16 text-gray-300 mx-auto mb-4" />
            <h3 className="text-lg font-semibold text-gray-900">No Interviews Found</h3>
            <p className="text-gray-500 mt-2">
              {interviews.length === 0 
                ? "Get started by creating your first interview"
                : "No interviews match your current filters"}
            </p>
            {interviews.length === 0 && (
              <Button className="mt-4" onClick={() => { resetForm(); setShowCreateModal(true); }}>
                <Plus className="w-4 h-4" /> Create Interview
              </Button>
            )}
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="w-full">
              <thead className="bg-gray-50 border-b">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Beneficiary
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Project
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Interviewer
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Date
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Score
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Actions
                  </th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {filteredInterviews.map((interview) => (
                  <tr key={interview.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div>
                        <div className="font-medium text-gray-900">{interview.beneficiary_name}</div>
                        <div className="text-sm text-gray-500">{interview.beneficiary_id}</div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className="px-2 py-1 bg-gray-100 text-gray-800 rounded text-sm font-mono">
                        {interview.project_code}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-600">
                      {interview.interviewer_name || '-'}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-600">
                      <div className="flex items-center gap-1">
                        <Calendar className="w-4 h-4 text-gray-400" />
                        {interview.interview_date}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {getStatusBadge(interview.status)}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      {interview.impact_score ? (
                        <span className={`font-semibold ${getScoreColor(interview.impact_score)}`}>
                          {interview.impact_score}/5
                        </span>
                      ) : (
                        <span className="text-gray-400">-</span>
                      )}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center gap-2">
                        {(interview.status === 'draft' || interview.status === 'in_progress') && (
                          <button
                            onClick={() => handleConductInterview(interview)}
                            className="p-1.5 text-emerald-600 hover:bg-emerald-50 rounded"
                            title="Continue Interview"
                          >
                            <Play className="w-4 h-4" />
                          </button>
                        )}
                        <button
                          onClick={() => handleViewInterview(interview)}
                          className="p-1.5 text-blue-600 hover:bg-blue-50 rounded"
                          title="View Details"
                        >
                          <Eye className="w-4 h-4" />
                        </button>
                        {interview.status === 'submitted' && (
                          <button
                            onClick={() => handleReviewInterview(interview)}
                            className="p-1.5 text-purple-600 hover:bg-purple-50 rounded"
                            title="Review"
                          >
                            <CheckCircle className="w-4 h-4" />
                          </button>
                        )}
                        {interview.status === 'draft' && (
                          <button
                            onClick={() => handleDeleteInterview(interview)}
                            className="p-1.5 text-red-600 hover:bg-red-50 rounded"
                            title="Delete"
                          >
                            <Trash2 className="w-4 h-4" />
                          </button>
                        )}
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </Card>

      {/* Create Interview Modal */}
      <Modal isOpen={showCreateModal} onClose={() => setShowCreateModal(false)} title="Create New Interview">
        <div className="space-y-4">
          <Select
            label="Project *"
            name="project"
            value={formData.project}
            onChange={(e) => handleProjectChange(e.target.value)}
            options={[
              { value: '', label: 'Select a project' },
              ...projects.map(p => ({ value: p.id.toString(), label: `${p.code} - ${p.title}` }))
            ]}
            required
          />

          {formData.project && (
            <>
              <Select
                label="Beneficiary *"
                name="beneficiary"
                value={formData.beneficiary}
                onChange={handleInputChange}
                options={[
                  { value: '', label: 'Select a beneficiary' },
                  ...beneficiaries.map(b => ({ 
                    value: b.id.toString(), 
                    label: `${b.name} (${b.beneficiary_id})` 
                  }))
                ]}
                required
              />

              {questionnaires.length > 0 && (
                <Select
                  label="Questionnaire"
                  name="questionnaire"
                  value={formData.questionnaire}
                  onChange={handleInputChange}
                  options={[
                    { value: '', label: 'Select a questionnaire (optional)' },
                    ...questionnaires.map(q => ({ value: q.id.toString(), label: q.name || q.template_name }))
                  ]}
                />
              )}
            </>
          )}

          <Select
            label="Field Resource (Interviewer)"
            name="interviewer"
            value={formData.interviewer}
            onChange={handleInputChange}
            options={[
              { value: '', label: 'Select interviewer (or leave blank for self)' },
              ...fieldResources.map(r => ({ 
                value: r.id.toString(), 
                label: r.full_name || `${r.first_name} ${r.last_name}` 
              }))
            ]}
          />

          <Input
            label="Interview Date *"
            name="interview_date"
            type="date"
            value={formData.interview_date}
            onChange={handleInputChange}
            required
          />

          <Input
            label="Interview Location"
            name="interview_location"
            value={formData.interview_location}
            onChange={handleInputChange}
            placeholder="e.g., Village Center, Beneficiary Home"
          />

          <Textarea
            label="Notes"
            name="interviewer_notes"
            value={formData.interviewer_notes}
            onChange={handleInputChange}
            rows={3}
            placeholder="Any initial notes or observations"
          />

          <div className="flex justify-end gap-3 pt-4 border-t">
            <Button variant="secondary" onClick={() => setShowCreateModal(false)}>
              Cancel
            </Button>
            <Button onClick={handleCreateInterview} disabled={submitting}>
              {submitting ? 'Creating...' : 'Create & Start Interview'}
            </Button>
          </div>
        </div>
      </Modal>

      {/* Conduct Interview Modal */}
      <Modal 
        isOpen={showConductModal} 
        onClose={() => setShowConductModal(false)} 
        title={`Interview: ${selectedInterview?.beneficiary_name || ''}`}
      >
        <div className="space-y-4">
          {/* Beneficiary Info */}
          {selectedInterview && (
            <Card className="p-4 bg-gray-50">
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                <div>
                  <span className="text-gray-500">Project:</span>
                  <span className="ml-2 font-medium">{selectedInterview.project_code}</span>
                </div>
                <div>
                  <span className="text-gray-500">Date:</span>
                  <span className="ml-2 font-medium">{selectedInterview.interview_date}</span>
                </div>
                <div>
                  <span className="text-gray-500">Location:</span>
                  <span className="ml-2 font-medium">{selectedInterview.interview_location || '-'}</span>
                </div>
                <div>
                  <span className="text-gray-500">Status:</span>
                  <span className="ml-2">{getStatusBadge(selectedInterview.status)}</span>
                </div>
              </div>
            </Card>
          )}

          {/* Section Navigation */}
          {selectedTemplate?.sections && selectedTemplate.sections.length > 0 && (
            <div className="border rounded-lg overflow-hidden">
              <div className="bg-gray-100 p-3 flex items-center justify-between">
                <h3 className="font-semibold">Sections</h3>
                <span className="text-sm text-gray-500">
                  {currentSectionIndex + 1} of {selectedTemplate.sections.length}
                </span>
              </div>
              <div className="flex overflow-x-auto p-2 gap-2">
                {selectedTemplate.sections.map((section, idx) => {
                  const progress = getSectionProgress(section);
                  return (
                    <button
                      key={section.id}
                      onClick={() => setCurrentSectionIndex(idx)}
                      className={`flex-shrink-0 px-4 py-2 rounded-lg text-sm font-medium transition-all ${
                        idx === currentSectionIndex
                          ? 'bg-emerald-600 text-white'
                          : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                      }`}
                    >
                      <div>{section.title}</div>
                      <div className="text-xs opacity-75">
                        {progress.answered}/{progress.total}
                      </div>
                    </button>
                  );
                })}
              </div>
            </div>
          )}

          {/* Questions */}
          {selectedTemplate?.sections && selectedTemplate.sections[currentSectionIndex] && (
            <div className="space-y-6 max-h-96 overflow-y-auto">
              <div className="border-b pb-2">
                <h3 className="font-semibold text-lg">
                  {selectedTemplate.sections[currentSectionIndex].title}
                </h3>
                {selectedTemplate.sections[currentSectionIndex].description && (
                  <p className="text-sm text-gray-500 mt-1">
                    {selectedTemplate.sections[currentSectionIndex].description}
                  </p>
                )}
              </div>

              {selectedTemplate.sections[currentSectionIndex].questions.map((question, qIdx) => (
                <div key={question.id} className="border rounded-lg p-4">
                  <div className="flex items-start gap-3 mb-3">
                    <span className="flex-shrink-0 w-7 h-7 bg-emerald-100 text-emerald-700 rounded-full flex items-center justify-center text-sm font-medium">
                      {qIdx + 1}
                    </span>
                    <div className="flex-1">
                      <p className="font-medium text-gray-900">
                        {question.text}
                        {question.is_required && <span className="text-red-500 ml-1">*</span>}
                      </p>
                      {question.help_text && (
                        <p className="text-sm text-gray-500 mt-1">{question.help_text}</p>
                      )}
                    </div>
                  </div>
                  <div className="ml-10">
                    {renderQuestionInput(question)}
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* No Template Selected */}
          {(!selectedTemplate || !selectedTemplate.sections || selectedTemplate.sections.length === 0) && (
            <div className="text-center py-8">
              <ClipboardList className="w-12 h-12 text-gray-300 mx-auto mb-4" />
              <p className="text-gray-500">No questionnaire template available.</p>
              <p className="text-sm text-gray-400 mt-2">
                Please create a questionnaire template first.
              </p>
            </div>
          )}

          {/* Navigation & Save */}
          <div className="flex items-center justify-between pt-4 border-t">
            <div className="flex gap-2">
              <Button
                variant="secondary"
                onClick={() => setCurrentSectionIndex(prev => Math.max(0, prev - 1))}
                disabled={currentSectionIndex === 0}
              >
                Previous
              </Button>
              {selectedTemplate?.sections && currentSectionIndex < selectedTemplate.sections.length - 1 && (
                <Button
                  variant="secondary"
                  onClick={() => setCurrentSectionIndex(prev => prev + 1)}
                >
                  Next
                </Button>
              )}
            </div>
            <div className="flex gap-2">
              <Button variant="secondary" onClick={() => handleSaveAnswers('in_progress')} disabled={submitting}>
                {submitting ? 'Saving...' : 'Save Draft'}
              </Button>
              <Button onClick={() => handleSaveAnswers('submitted')} disabled={submitting}>
                {submitting ? 'Submitting...' : 'Submit Interview'}
              </Button>
            </div>
          </div>
        </div>
      </Modal>

      {/* View Interview Modal */}
      <Modal 
        isOpen={showViewModal} 
        onClose={() => setShowViewModal(false)} 
        title={`Interview Details`}
      >
        {selectedInterview && (
          <div className="space-y-6">
            {/* Header Info */}
            <div className="grid grid-cols-2 gap-4">
              <Card className="p-4">
                <h4 className="text-sm font-semibold text-gray-500 mb-2">Beneficiary</h4>
                <p className="font-medium text-lg">{selectedInterview.beneficiary_name}</p>
                <p className="text-sm text-gray-500">{selectedInterview.beneficiary_id}</p>
                {selectedInterview.beneficiary_details && (
                  <div className="mt-3 text-sm text-gray-600 space-y-1">
                    <p><User className="w-4 h-4 inline mr-1" /> {selectedInterview.beneficiary_details.gender}, {selectedInterview.beneficiary_details.age} yrs</p>
                    <p><MapPin className="w-4 h-4 inline mr-1" /> {selectedInterview.beneficiary_details.village}, {selectedInterview.beneficiary_details.district}</p>
                  </div>
                )}
              </Card>
              <Card className="p-4">
                <h4 className="text-sm font-semibold text-gray-500 mb-2">Interview Info</h4>
                <div className="space-y-2 text-sm">
                  <p><Calendar className="w-4 h-4 inline mr-1" /> {selectedInterview.interview_date}</p>
                  <p><User className="w-4 h-4 inline mr-1" /> {selectedInterview.interviewer_name}</p>
                  <p><MapPin className="w-4 h-4 inline mr-1" /> {selectedInterview.interview_location || '-'}</p>
                  <p>Status: {getStatusBadge(selectedInterview.status)}</p>
                  {selectedInterview.duration_minutes && (
                    <p><Clock className="w-4 h-4 inline mr-1" /> {selectedInterview.duration_minutes} minutes</p>
                  )}
                </div>
              </Card>
            </div>

            {/* Scores */}
            {(selectedInterview.impact_score || selectedInterview.satisfaction_score) && (
              <Card className="p-4">
                <h4 className="text-sm font-semibold text-gray-500 mb-3">Scores</h4>
                <div className="flex gap-8">
                  {selectedInterview.impact_score && (
                    <div>
                      <span className="text-gray-500">Impact Score:</span>
                      <span className={`ml-2 text-xl font-bold ${getScoreColor(selectedInterview.impact_score)}`}>
                        {selectedInterview.impact_score}/5
                      </span>
                    </div>
                  )}
                  {selectedInterview.satisfaction_score && (
                    <div>
                      <span className="text-gray-500">Satisfaction:</span>
                      <span className={`ml-2 text-xl font-bold ${getScoreColor(selectedInterview.satisfaction_score)}`}>
                        {selectedInterview.satisfaction_score}/5
                      </span>
                    </div>
                  )}
                </div>
              </Card>
            )}

            {/* Answers */}
            {selectedInterview.answers && selectedInterview.answers.length > 0 && (
              <div className="space-y-4">
                <h4 className="font-semibold">Responses ({selectedInterview.answers.length})</h4>
                <div className="max-h-64 overflow-y-auto space-y-3">
                  {selectedInterview.answers.map((answer, idx) => (
                    <Card key={answer.id || idx} className="p-3">
                      <p className="text-sm text-gray-500">{answer.section_title}</p>
                      <p className="font-medium">{answer.question_text}</p>
                      <p className="text-emerald-700 mt-1">{answer.display_value || '-'}</p>
                    </Card>
                  ))}
                </div>
              </div>
            )}

            {/* Notes & Observations */}
            {(selectedInterview.interviewer_notes || selectedInterview.observations) && (
              <Card className="p-4">
                <h4 className="text-sm font-semibold text-gray-500 mb-2">Notes & Observations</h4>
                {selectedInterview.interviewer_notes && (
                  <p className="text-gray-700 mb-2">{selectedInterview.interviewer_notes}</p>
                )}
                {selectedInterview.observations && (
                  <p className="text-gray-600 italic">{selectedInterview.observations}</p>
                )}
              </Card>
            )}

            {/* Review Info */}
            {selectedInterview.reviewed_by_name && (
              <Card className="p-4 bg-gray-50">
                <h4 className="text-sm font-semibold text-gray-500 mb-2">Review</h4>
                <p className="text-sm">
                  Reviewed by <span className="font-medium">{selectedInterview.reviewed_by_name}</span>
                  {selectedInterview.reviewed_at && ` on ${new Date(selectedInterview.reviewed_at).toLocaleDateString()}`}
                </p>
                {selectedInterview.review_comments && (
                  <p className="text-gray-600 mt-2">{selectedInterview.review_comments}</p>
                )}
              </Card>
            )}

            <div className="flex justify-end gap-3 pt-4 border-t">
              <Button variant="secondary" onClick={() => setShowViewModal(false)}>
                Close
              </Button>
              {selectedInterview.status === 'submitted' && (
                <Button onClick={() => { setShowViewModal(false); handleReviewInterview(selectedInterview); }}>
                  Review Interview
                </Button>
              )}
            </div>
          </div>
        )}
      </Modal>

      {/* Review Modal */}
      <Modal 
        isOpen={showReviewModal} 
        onClose={() => setShowReviewModal(false)} 
        title="Review Interview"
      >
        {selectedInterview && (
          <div className="space-y-6">
            <Card className="p-4 bg-gray-50">
              <div className="flex justify-between items-start">
                <div>
                  <h3 className="font-semibold">{selectedInterview.beneficiary_name}</h3>
                  <p className="text-sm text-gray-500">{selectedInterview.project_code}</p>
                </div>
                <div className="text-right">
                  <p className="text-sm text-gray-500">Date: {selectedInterview.interview_date}</p>
                  <p className="text-sm text-gray-500">By: {selectedInterview.interviewer_name}</p>
                </div>
              </div>
            </Card>

            {selectedInterview.impact_score && (
              <div className="flex items-center justify-center py-4">
                <div className="text-center">
                  <p className="text-sm text-gray-500 mb-1">Impact Score</p>
                  <p className={`text-4xl font-bold ${getScoreColor(selectedInterview.impact_score)}`}>
                    {selectedInterview.impact_score}/5
                  </p>
                </div>
              </div>
            )}

            <Select
              label="Decision *"
              name="status"
              value={reviewData.status}
              onChange={(e) => setReviewData(prev => ({ ...prev, status: e.target.value }))}
              options={[
                { value: '', label: 'Select decision' },
                { value: 'approved', label: ' Approve - Data is complete and accurate' },
                { value: 'rejected', label: ' Reject - Data needs correction' },
              ]}
              required
            />

            <Textarea
              label="Review Comments"
              name="review_comments"
              value={reviewData.review_comments}
              onChange={(e) => setReviewData(prev => ({ ...prev, review_comments: e.target.value }))}
              rows={4}
              placeholder="Add any comments about this interview..."
            />

            <div className="flex justify-end gap-3 pt-4 border-t">
              <Button variant="secondary" onClick={() => setShowReviewModal(false)}>
                Cancel
              </Button>
              <Button 
                onClick={handleSubmitReview} 
                disabled={!reviewData.status || submitting}
                variant={reviewData.status === 'rejected' ? 'danger' : 'primary'}
              >
                {submitting ? 'Submitting...' : reviewData.status === 'approved' ? 'Approve Interview' : reviewData.status === 'rejected' ? 'Reject Interview' : 'Submit Review'}
              </Button>
            </div>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default InterviewsPage;
