import React, { useState, useEffect } from 'react';
import { Plus, ClipboardList, Edit, Eye, Trash2, Copy, ChevronDown, ChevronUp, X } from 'lucide-react';
import api, { endpoints } from '../api';
import { Card, Button, Input, Select, Modal, Badge, Textarea } from '../components/ui';

interface Question {
  id?: number;
  question_text: string;
  question_type: string;
  is_required: boolean;
  order: number;
  options?: string;
  help_text?: string;
}

interface Section {
  id?: number;
  title: string;
  description: string;
  order: number;
  questions?: Question[];
  question_count?: number;
}

interface QuestionnaireTemplate {
  id: number;
  name: string;
  description: string;
  sector: string;
  sector_display?: string;
  version?: string;
  is_default: boolean;
  is_active: boolean;
  section_count?: number;
  question_count?: number;
  sections?: Section[];
  created_by?: number;
  created_by_name?: string;
  created_at?: string;
}

const QuestionnairesPage: React.FC = () => {
  const [templates, setTemplates] = useState<QuestionnaireTemplate[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [showCreateModal, setShowCreateModal] = useState<boolean>(false);
  const [showBuilderModal, setShowBuilderModal] = useState<boolean>(false);
  const [showViewModal, setShowViewModal] = useState<boolean>(false);
  const [selectedTemplate, setSelectedTemplate] = useState<QuestionnaireTemplate | null>(null);
  const [isEditMode, setIsEditMode] = useState<boolean>(false);
  const [submitting, setSubmitting] = useState<boolean>(false);
  const [error, setError] = useState<string>('');

  // Form state
  const [formData, setFormData] = useState({
    id: null as number | null,
    name: '',
    description: '',
    sector: 'education',
    version: '1.0',
    is_default: false,
    is_active: true,
  });

  // Builder state
  const [sections, setSections] = useState<Section[]>([]);
  const [expandedSection, setExpandedSection] = useState<number | null>(null);

  const sectorOptions = [
    { value: 'education', label: 'Education' },
    { value: 'health', label: 'Health' },
    { value: 'livelihood', label: 'Livelihood' },
    { value: 'agriculture', label: 'Agriculture' },
    { value: 'nutrition', label: 'Nutrition' },
    { value: 'water_sanitation', label: 'Water & Sanitation' },
    { value: 'housing', label: 'Housing' },
    { value: 'skills_training', label: 'Skills Training' },
    { value: 'women_empowerment', label: 'Women Empowerment' },
    { value: 'child_welfare', label: 'Child Welfare' },
    { value: 'other', label: 'Other' },
  ];

  const questionTypeOptions = [
    { value: 'text', label: 'Short Text' },
    { value: 'textarea', label: 'Long Text' },
    { value: 'number', label: 'Number' },
    { value: 'email', label: 'Email' },
    { value: 'phone', label: 'Phone Number' },
    { value: 'date', label: 'Date' },
    { value: 'single_choice', label: 'Single Choice' },
    { value: 'multiple_choice', label: 'Multiple Choice' },
    { value: 'dropdown', label: 'Dropdown' },
    { value: 'rating', label: 'Rating Scale' },
    { value: 'yes_no', label: 'Yes/No' },
    { value: 'file_upload', label: 'File Upload' },
    { value: 'location', label: 'GPS Location' },
    { value: 'signature', label: 'Signature' },
  ];

  useEffect(() => {
    loadTemplates();
  }, []);

  const loadTemplates = async () => {
    try {
      const data = await api.get<QuestionnaireTemplate[]>(endpoints.templates);
      console.log('Loaded templates:', data);
      setTemplates(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error loading templates:', error);
      setError('Failed to load templates');
    }
    setLoading(false);
  };

  const loadTemplateDetails = async (templateId: number) => {
    try {
      const data = await api.get<QuestionnaireTemplate>(`${endpoints.templates}${templateId}/`);
      return data;
    } catch (error) {
      console.error('Error loading template details:', error);
      return null;
    }
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target;
    if (type === 'checkbox') {
      const checked = (e.target as HTMLInputElement).checked;
      setFormData(prev => ({ ...prev, [name]: checked }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const resetForm = () => {
    setFormData({
      id: null,
      name: '',
      description: '',
      sector: 'education',
      version: '1.0',
      is_default: false,
      is_active: true,
    });
    setSections([]);
    setIsEditMode(false);
    setError('');
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setSubmitting(true);
    setError('');

    try {
      const payload = {
        name: formData.name,
        description: formData.description,
        sector: formData.sector,
        version: formData.version,
        is_default: formData.is_default,
        is_active: formData.is_active,
      };

      let savedTemplate;
      if (isEditMode && formData.id) {
        savedTemplate = await api.patch(`${endpoints.templates}${formData.id}/`, payload);
      } else {
        savedTemplate = await api.post(endpoints.templates, payload);
      }

      resetForm();
      setShowCreateModal(false);
      loadTemplates();

      // Open builder if new template
      if (!isEditMode && savedTemplate.id) {
        setSelectedTemplate(savedTemplate);
        setShowBuilderModal(true);
      }
    } catch (err: any) {
      console.error('Error saving template:', err);
      const errorMessage = err.response?.data?.detail 
        || err.response?.data?.error 
        || JSON.stringify(err.response?.data || {})
        || err.message 
        || 'Failed to save template';
      setError(errorMessage);
    }
    setSubmitting(false);
  };

  const handleEdit = (template: QuestionnaireTemplate) => {
    setFormData({
      id: template.id,
      name: template.name || '',
      description: template.description || '',
      sector: template.sector || 'education',
      version: template.version || '1.0',
      is_default: template.is_default || false,
      is_active: template.is_active !== false,
    });
    setIsEditMode(true);
    setShowCreateModal(true);
  };

  const handleBuildQuestionnaire = async (template: QuestionnaireTemplate) => {
    const fullTemplate = await loadTemplateDetails(template.id);
    if (fullTemplate) {
      setSelectedTemplate(fullTemplate);
      setSections(fullTemplate.sections || []);
      setShowBuilderModal(true);
    }
  };

  const handleDuplicate = async (template: QuestionnaireTemplate) => {
    try {
      const payload = {
        name: `${template.name} (Copy)`,
        description: template.description,
        sector: template.sector,
        version: template.version || '1.0',
        is_default: false,
        is_active: true,
      };
      await api.post(endpoints.templates, payload);
      loadTemplates();
    } catch (error) {
      alert('Failed to duplicate template');
      console.error('Error duplicating template:', error);
    }
  };

  const handleDelete = async (template: QuestionnaireTemplate) => {
    if (!window.confirm(`Are you sure you want to delete template "${template.name}"?\n\nThis action cannot be undone.`)) {
      return;
    }

    try {
      await api.delete(`${endpoints.templates}${template.id}/`);
      loadTemplates();
    } catch (error) {
      alert('Failed to delete template');
      console.error('Error deleting template:', error);
    }
  };

  const addSection = () => {
    const newSection: Section = {
      title: '',
      description: '',
      order: sections.length + 1,
      questions: [],
    };
    setSections([...sections, newSection]);
    setExpandedSection(sections.length);
  };

  const updateSection = (index: number, field: keyof Section, value: any) => {
    const updated = [...sections];
    updated[index] = { ...updated[index], [field]: value };
    setSections(updated);
  };

  const removeSection = (index: number) => {
    const updated = sections.filter((_, i) => i !== index);
    setSections(updated);
  };

  const addQuestion = (sectionIndex: number) => {
    const updated = [...sections];
    if (!updated[sectionIndex].questions) {
      updated[sectionIndex].questions = [];
    }
    const newQuestion: Question = {
      question_text: '',
      question_type: 'text',
      is_required: false,
      order: (updated[sectionIndex].questions?.length || 0) + 1,
    };
    updated[sectionIndex].questions!.push(newQuestion);
    setSections(updated);
  };

  const updateQuestion = (sectionIndex: number, questionIndex: number, field: keyof Question, value: any) => {
    const updated = [...sections];
    if (updated[sectionIndex].questions) {
      updated[sectionIndex].questions![questionIndex] = {
        ...updated[sectionIndex].questions![questionIndex],
        [field]: value
      };
      setSections(updated);
    }
  };

  const removeQuestion = (sectionIndex: number, questionIndex: number) => {
    const updated = [...sections];
    if (updated[sectionIndex].questions) {
      updated[sectionIndex].questions = updated[sectionIndex].questions!.filter((_, i) => i !== questionIndex);
      setSections(updated);
    }
  };

  const saveSectionsAndQuestions = async () => {
    if (!selectedTemplate) return;

    setSubmitting(true);
    setError('');

    try {
      // Save sections
      for (const section of sections) {
        let sectionId = section.id;

        if (sectionId) {
          // Update existing section
          await api.patch(`${endpoints.sections}${sectionId}/`, {
            title: section.title,
            description: section.description,
            order: section.order,
          });
        } else {
          // Create new section
          const savedSection = await api.post(endpoints.sections, {
            template: selectedTemplate.id,
            title: section.title,
            description: section.description,
            order: section.order,
          });
          sectionId = savedSection.id;
        }

        // Save questions for this section
        if (section.questions) {
          for (const question of section.questions) {
            if (question.id) {
              // Update existing question
              await api.patch(`${endpoints.questions}${question.id}/`, {
                question_text: question.question_text,
                question_type: question.question_type,
                is_required: question.is_required,
                order: question.order,
                options: question.options,
                help_text: question.help_text,
              });
            } else {
              // Create new question
              await api.post(endpoints.questions, {
                section: sectionId,
                question_text: question.question_text,
                question_type: question.question_type,
                is_required: question.is_required,
                order: question.order,
                options: question.options,
                help_text: question.help_text,
              });
            }
          }
        }
      }

      alert('Questionnaire saved successfully!');
      setShowBuilderModal(false);
      loadTemplates();
    } catch (err: any) {
      console.error('Error saving questionnaire:', err);
      alert('Failed to save questionnaire. Please try again.');
    }
    setSubmitting(false);
  };

  const getSectorColor = (sector: string) => {
    const colors: Record<string, string> = {
      education: 'bg-blue-100 text-blue-700',
      health: 'bg-red-100 text-red-700',
      livelihood: 'bg-green-100 text-green-700',
      agriculture: 'bg-yellow-100 text-yellow-700',
      nutrition: 'bg-orange-100 text-orange-700',
      water_sanitation: 'bg-cyan-100 text-cyan-700',
      housing: 'bg-purple-100 text-purple-700',
      skills_training: 'bg-indigo-100 text-indigo-700',
      women_empowerment: 'bg-pink-100 text-pink-700',
      child_welfare: 'bg-rose-100 text-rose-700',
      other: 'bg-gray-100 text-gray-700',
    };
    return colors[sector] || colors.other;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-gray-500">Loading templates...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Questionnaires</h1>
          <p className="text-gray-500 mt-1">Manage survey templates and questions</p>
        </div>
        <Button onClick={() => {
          resetForm();
          setShowCreateModal(true);
        }}>
          <Plus className="w-4 h-4" /> New Template
        </Button>
      </div>

      {error && !showCreateModal && !showBuilderModal && (
        <div className="bg-red-50 text-red-600 p-4 rounded-lg">
          {error}
        </div>
      )}

      {/* Templates Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {templates.map((template) => (
          <Card key={template.id} className="p-6 hover:shadow-lg transition-shadow">
            <div className="flex items-start gap-4">
              <div className="w-12 h-12 bg-emerald-100 rounded-lg flex items-center justify-center flex-shrink-0">
                <ClipboardList className="w-6 h-6 text-emerald-600" />
              </div>
              <div className="flex-1 min-w-0">
                <div className="flex items-start justify-between gap-2">
                  <h3 className="font-semibold text-gray-900 truncate">{template.name}</h3>
                  {template.is_default && (
                    <span className="text-xs flex-shrink-0">
                      <Badge variant="info">Default</Badge>
                    </span>
                  )}
                </div>
                <p className="text-sm text-gray-600 mt-1">
                  <span className={`inline-block px-2 py-0.5 rounded text-xs font-medium ${getSectorColor(template.sector)}`}>
                    {template.sector_display || template.sector}
                  </span>
                </p>
                <p className="text-sm text-gray-500 mt-2 line-clamp-2">{template.description}</p>

                <div className="flex items-center gap-4 mt-4 text-xs text-gray-500">
                  <span>{template.section_count || 0} sections</span>
                  <span>{template.question_count || 0} questions</span>
                </div>

                <div className="flex flex-wrap items-center gap-2 mt-4 pt-4 border-t">
                  <button
                    onClick={() => handleBuildQuestionnaire(template)}
                    className="flex items-center gap-1 text-sm text-emerald-600 hover:text-emerald-800 font-medium"
                  >
                    <Edit className="w-3.5 h-3.5" /> Build
                  </button>
                  <button
                    onClick={() => {
                      loadTemplateDetails(template.id).then(full => {
                        if (full) {
                          setSelectedTemplate(full);
                          setShowViewModal(true);
                        }
                      });
                    }}
                    className="flex items-center gap-1 text-sm text-blue-600 hover:text-blue-800"
                  >
                    <Eye className="w-3.5 h-3.5" /> View
                  </button>
                  <button
                    onClick={() => handleEdit(template)}
                    className="flex items-center gap-1 text-sm text-purple-600 hover:text-purple-800"
                  >
                    <Edit className="w-3.5 h-3.5" /> Edit
                  </button>
                  <button
                    onClick={() => handleDuplicate(template)}
                    className="flex items-center gap-1 text-sm text-gray-600 hover:text-gray-800"
                  >
                    <Copy className="w-3.5 h-3.5" />
                  </button>
                  {!template.is_default && (
                    <button
                      onClick={() => handleDelete(template)}
                      className="flex items-center gap-1 text-sm text-red-600 hover:text-red-800 ml-auto"
                    >
                      <Trash2 className="w-3.5 h-3.5" />
                    </button>
                  )}
                </div>
              </div>
            </div>
          </Card>
        ))}

        {/* Create New Card */}
        <Card 
          className="p-6 border-2 border-dashed border-gray-300 hover:border-emerald-500 hover:bg-emerald-50 transition-all cursor-pointer flex items-center justify-center min-h-[280px]"
          onClick={() => {
            resetForm();
            setShowCreateModal(true);
          }}
        >
          <div className="text-center">
            <div className="w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-3">
              <Plus className="w-6 h-6 text-gray-400" />
            </div>
            <p className="text-gray-600 font-medium">Create New Template</p>
            <p className="text-sm text-gray-500 mt-1">Start from scratch</p>
          </div>
        </Card>
      </div>

      {/* View Template Modal */}
      {selectedTemplate && showViewModal && (
        <Modal 
          isOpen={showViewModal} 
          onClose={() => {
            setShowViewModal(false);
            setSelectedTemplate(null);
          }} 
          title="Template Details"
        >
          <div className="space-y-4 max-h-[70vh] overflow-y-auto">
            <div>
              <label className="text-sm text-gray-500">Template Name</label>
              <p className="font-medium">{selectedTemplate.name}</p>
            </div>

            <div>
              <label className="text-sm text-gray-500">Description</label>
              <p className="text-gray-700">{selectedTemplate.description}</p>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-sm text-gray-500">Sector</label>
                <p className="font-medium">
                  <span className={`inline-block px-2 py-1 rounded text-sm ${getSectorColor(selectedTemplate.sector)}`}>
                    {selectedTemplate.sector_display || selectedTemplate.sector}
                  </span>
                </p>
              </div>
              <div>
                <label className="text-sm text-gray-500">Version</label>
                <p className="font-medium">{selectedTemplate.version || 'N/A'}</p>
              </div>
            </div>

            <div className="grid grid-cols-3 gap-4">
              <div>
                <label className="text-sm text-gray-500">Sections</label>
                <p className="font-medium">{selectedTemplate.section_count || selectedTemplate.sections?.length || 0}</p>
              </div>
              <div>
                <label className="text-sm text-gray-500">Questions</label>
                <p className="font-medium">{selectedTemplate.question_count || 0}</p>
              </div>
              <div>
                <label className="text-sm text-gray-500">Status</label>
                <p className="font-medium">
                  {selectedTemplate.is_active ? (
                    <Badge variant="success">Active</Badge>
                  ) : (
                    <Badge variant="default">Inactive</Badge>
                  )}
                </p>
              </div>
            </div>

            {selectedTemplate.sections && selectedTemplate.sections.length > 0 && (
              <div>
                <label className="text-sm text-gray-500 mb-2 block">Sections & Questions</label>
                <div className="space-y-3">
                  {selectedTemplate.sections.map((section, idx) => (
                    <div key={section.id || idx} className="border rounded-lg p-3">
                      <h4 className="font-medium text-gray-900">{section.title}</h4>
                      <p className="text-sm text-gray-600">{section.description}</p>
                      <p className="text-xs text-gray-500 mt-2">
                        {section.question_count || section.questions?.length || 0} questions
                      </p>
                    </div>
                  ))}
                </div>
              </div>
            )}

            <div className="flex gap-3 pt-4 border-t sticky bottom-0 bg-white">
              <Button variant="secondary" onClick={() => {
                setShowViewModal(false);
                setSelectedTemplate(null);
              }}>
                Close
              </Button>
              <Button onClick={() => {
                setShowViewModal(false);
                handleBuildQuestionnaire(selectedTemplate);
              }}>
                <Edit className="w-4 h-4" /> Edit Questionnaire
              </Button>
            </div>
          </div>
        </Modal>
      )}

      {/* Create/Edit Basic Info Modal */}
      <Modal 
        isOpen={showCreateModal} 
        onClose={() => {
          setShowCreateModal(false);
          resetForm();
        }} 
        title={isEditMode ? "Edit Template" : "Create New Template"}
      >
        <form className="space-y-4" onSubmit={handleSubmit}>
          {error && (
            <div className="bg-red-50 text-red-600 p-3 rounded-lg text-sm">
              {error}
            </div>
          )}

          <Input 
            label="Template Name *" 
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            placeholder="e.g., Education Scholarship Assessment"
            required 
          />

          <Textarea 
            label="Description *" 
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            placeholder="Brief description of the questionnaire purpose"
            required
            rows={3}
          />

          <div className="grid grid-cols-2 gap-4">
            <Select 
              label="Sector *"
              name="sector"
              value={formData.sector}
              onChange={handleInputChange}
              options={sectorOptions}
            />

            <Input 
              label="Version" 
              name="version"
              value={formData.version}
              onChange={handleInputChange}
              placeholder="1.0"
            />
          </div>

          <div className="space-y-2">
            <label className="flex items-center gap-2 cursor-pointer">
              <input
                type="checkbox"
                name="is_default"
                checked={formData.is_default}
                onChange={handleInputChange}
                className="w-4 h-4 text-emerald-600 border-gray-300 rounded focus:ring-emerald-500"
              />
              <span className="text-sm text-gray-700">Set as default template for this sector</span>
            </label>

            <label className="flex items-center gap-2 cursor-pointer">
              <input
                type="checkbox"
                name="is_active"
                checked={formData.is_active}
                onChange={handleInputChange}
                className="w-4 h-4 text-emerald-600 border-gray-300 rounded focus:ring-emerald-500"
              />
              <span className="text-sm text-gray-700">Active (available for use)</span>
            </label>
          </div>

          <div className="flex gap-3 pt-4 border-t">
            <Button 
              variant="secondary" 
              type="button" 
              onClick={() => {
                setShowCreateModal(false);
                resetForm();
              }}
              disabled={submitting}
            >
              Cancel
            </Button>
            <Button type="submit" disabled={submitting}>
              {submitting ? (isEditMode ? 'Updating...' : 'Creating...') : (isEditMode ? 'Update' : 'Create & Build')}
            </Button>
          </div>
        </form>
      </Modal>

      {/* Questionnaire Builder Modal */}
      {selectedTemplate && showBuilderModal && (
        <Modal 
          isOpen={showBuilderModal} 
          onClose={() => {
            setShowBuilderModal(false);
            setSections([]);
            setSelectedTemplate(null);
          }} 
          title={`Build: ${selectedTemplate.name}`}
        >
          <div className="space-y-4 max-h-[70vh] overflow-y-auto pr-2">
            <div className="bg-blue-50 p-3 rounded-lg text-sm text-blue-700">
              Add sections and questions to build your questionnaire
            </div>

            {/* Sections */}
            <div className="space-y-3">
              {sections.map((section, sIndex) => (
                <div key={sIndex} className="border rounded-lg">
                  {/* Section Header */}
                  <div 
                    className="flex items-center justify-between p-3 bg-gray-50 cursor-pointer"
                    onClick={() => setExpandedSection(expandedSection === sIndex ? null : sIndex)}
                  >
                    <div className="flex items-center gap-2">
                      {expandedSection === sIndex ? <ChevronUp className="w-4 h-4" /> : <ChevronDown className="w-4 h-4" />}
                      <span className="font-medium">
                        {section.title || `Section ${sIndex + 1}`}
                      </span>
                      <span className="text-xs text-gray-500">
                        ({section.questions?.length || 0} questions)
                      </span>
                    </div>
                    <button
                      type="button"
                      onClick={(e) => {
                        e.stopPropagation();
                        removeSection(sIndex);
                      }}
                      className="text-red-600 hover:text-red-800"
                    >
                      <X className="w-4 h-4" />
                    </button>
                  </div>

                  {/* Section Content */}
                  {expandedSection === sIndex && (
                    <div className="p-3 space-y-3">
                      <Input 
                        label="Section Title *"
                        value={section.title}
                        onChange={(e) => updateSection(sIndex, 'title', e.target.value)}
                        placeholder="e.g., Demographic Information"
                        required
                      />

                      <Textarea 
                        label="Section Description"
                        value={section.description}
                        onChange={(e) => updateSection(sIndex, 'description', e.target.value)}
                        placeholder="Optional description"
                        rows={2}
                      />

                      {/* Questions */}
                      <div className="space-y-2">
                        <label className="text-sm font-medium text-gray-700">Questions</label>
                        {section.questions?.map((question, qIndex) => (
                          <div key={qIndex} className="border rounded p-3 bg-white space-y-2">
                            <div className="flex items-start justify-between gap-2">
                              <Input 
                                label={`Question ${qIndex + 1} *`}
                                value={question.question_text}
                                onChange={(e) => updateQuestion(sIndex, qIndex, 'question_text', e.target.value)}
                                placeholder="Enter your question"
                                required
                                className="flex-1"
                              />
                              <button
                                type="button"
                                onClick={() => removeQuestion(sIndex, qIndex)}
                                className="text-red-600 hover:text-red-800 mt-6"
                              >
                                <X className="w-4 h-4" />
                              </button>
                            </div>

                            <div className="grid grid-cols-2 gap-2">
                              <Select 
                                label="Type"
                                value={question.question_type}
                                onChange={(e) => updateQuestion(sIndex, qIndex, 'question_type', e.target.value)}
                                options={questionTypeOptions}
                              />
                              <label className="flex items-center gap-2 mt-6">
                                <input
                                  type="checkbox"
                                  checked={question.is_required}
                                  onChange={(e) => updateQuestion(sIndex, qIndex, 'is_required', e.target.checked)}
                                  className="w-4 h-4 text-emerald-600 border-gray-300 rounded"
                                />
                                <span className="text-sm text-gray-700">Required</span>
                              </label>
                            </div>

                            {['single_choice', 'multiple_choice', 'dropdown'].includes(question.question_type) && (
                              <Textarea 
                                label="Options (one per line)"
                                value={question.options || ''}
                                onChange={(e) => updateQuestion(sIndex, qIndex, 'options', e.target.value)}
                                placeholder="Option 1&#10;Option 2&#10;Option 3"
                                rows={3}
                              />
                            )}

                            <Input 
                              label="Help Text (optional)"
                              value={question.help_text || ''}
                              onChange={(e) => updateQuestion(sIndex, qIndex, 'help_text', e.target.value)}
                              placeholder="Additional guidance for respondents"
                            />
                          </div>
                        ))}

                        <Button 
                          type="button"
                          variant="secondary" 
                          size="sm"
                          onClick={() => addQuestion(sIndex)}
                        >
                          <Plus className="w-3 h-3" /> Add Question
                        </Button>
                      </div>
                    </div>
                  )}
                </div>
              ))}

              <Button 
                type="button"
                variant="secondary"
                onClick={addSection}
                className="w-full"
              >
                <Plus className="w-4 h-4" /> Add Section
              </Button>
            </div>

            {/* Actions */}
            <div className="flex gap-3 pt-4 border-t sticky bottom-0 bg-white">
              <Button 
                variant="secondary"
                type="button"
                onClick={() => {
                  setShowBuilderModal(false);
                  setSections([]);
                  setSelectedTemplate(null);
                }}
                disabled={submitting}
              >
                Cancel
              </Button>
              <Button 
                type="button"
                onClick={saveSectionsAndQuestions}
                disabled={submitting || sections.length === 0}
              >
                {submitting ? 'Saving...' : 'Save Questionnaire'}
              </Button>
            </div>
          </div>
        </Modal>
      )}
    </div>
  );
};

export default QuestionnairesPage;
