import React, { useState, useEffect } from 'react';
import { Plus, ClipboardList, Edit, Eye, Trash2, Copy } from 'lucide-react';
import api, { endpoints } from '../api';
import { Card, Button, Input, Select, Modal, Badge, Textarea } from '../components/ui';

interface QuestionnaireTemplate {
  id: number;
  title: string;
  description: string;
  sector: string;
  sector_display?: string;
  is_default: boolean;
  questions_count?: number;
  sections_count?: number;
  created_by?: number;
  created_by_name?: string;
  created_at?: string;
  updated_at?: string;
}

interface Question {
  id?: number;
  question_text: string;
  question_type: string;
  is_required: boolean;
  order: number;
  options?: string[];
  validation_rules?: any;
}

interface Section {
  id?: number;
  title: string;
  description: string;
  order: number;
  questions: Question[];
}

const QuestionnairesPage: React.FC = () => {
  const [templates, setTemplates] = useState<QuestionnaireTemplate[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [showCreateModal, setShowCreateModal] = useState<boolean>(false);
  const [showViewModal, setShowViewModal] = useState<boolean>(false);
  const [selectedTemplate, setSelectedTemplate] = useState<QuestionnaireTemplate | null>(null);
  const [isEditMode, setIsEditMode] = useState<boolean>(false);
  const [submitting, setSubmitting] = useState<boolean>(false);
  const [error, setError] = useState<string>('');

  // Form state
  const [formData, setFormData] = useState({
    id: null as number | null,
    title: '',
    description: '',
    sector: 'education',
    is_default: false,
  });

  // Sector options
  const sectorOptions = [
    { value: 'education', label: 'Education' },
    { value: 'health', label: 'Health' },
    { value: 'livelihood', label: 'Livelihood' },
    { value: 'agriculture', label: 'Agriculture' },
    { value: 'nutrition', label: 'Nutrition' },
    { value: 'water_sanitation', label: 'Water & Sanitation' },
    { value: 'housing', label: 'Housing' },
    { value: 'skills_training', label: 'Skills Training' },
    { value: 'women_empowerment', label: 'Women Empowerment' },
    { value: 'child_welfare', label: 'Child Welfare' },
    { value: 'other', label: 'Other' },
  ];

  useEffect(() => {
    loadTemplates();
  }, []);

  const loadTemplates = async () => {
    try {
      const data = await api.get<QuestionnaireTemplate[]>(endpoints.templates);
      setTemplates(Array.isArray(data) ? data : []);
    } catch (error) {
      console.error('Error loading templates:', error);
    }
    setLoading(false);
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value, type } = e.target;
    if (type === 'checkbox') {
      const checked = (e.target as HTMLInputElement).checked;
      setFormData(prev => ({ ...prev, [name]: checked }));
    } else {
      setFormData(prev => ({ ...prev, [name]: value }));
    }
  };

  const resetForm = () => {
    setFormData({
      id: null,
      title: '',
      description: '',
      sector: 'education',
      is_default: false,
    });
    setIsEditMode(false);
    setError('');
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setSubmitting(true);
    setError('');

    try {
      const payload = {
        title: formData.title,
        description: formData.description,
        sector: formData.sector,
        is_default: formData.is_default,
      };

      if (isEditMode && formData.id) {
        await api.patch(`${endpoints.templates}${formData.id}/`, payload);
      } else {
        await api.post(endpoints.templates, payload);
      }

      resetForm();
      setShowCreateModal(false);
      loadTemplates();
    } catch (err: any) {
      console.error('Error saving template:', err);
      const errorMessage = err.response?.data?.detail 
        || err.response?.data?.error 
        || Object.values(err.response?.data || {}).flat().join(', ')
        || err.message 
        || 'Failed to save template';
      setError(errorMessage);
    }
    setSubmitting(false);
  };

  const handleEdit = (template: QuestionnaireTemplate) => {
    setFormData({
      id: template.id,
      title: template.title || '',
      description: template.description || '',
      sector: template.sector || 'education',
      is_default: template.is_default || false,
    });
    setIsEditMode(true);
    setShowCreateModal(true);
  };

  const handleDuplicate = async (template: QuestionnaireTemplate) => {
    try {
      const payload = {
        title: `${template.title} (Copy)`,
        description: template.description,
        sector: template.sector,
        is_default: false,
      };
      await api.post(endpoints.templates, payload);
      loadTemplates();
    } catch (error) {
      alert('Failed to duplicate template');
      console.error('Error duplicating template:', error);
    }
  };

  const handleDelete = async (template: QuestionnaireTemplate) => {
    if (!window.confirm(`Are you sure you want to delete template "${template.title}"?\n\nThis action cannot be undone.`)) {
      return;
    }

    try {
      await api.delete(`${endpoints.templates}${template.id}/`);
      loadTemplates();
    } catch (error) {
      alert('Failed to delete template');
      console.error('Error deleting template:', error);
    }
  };

  const getSectorColor = (sector: string) => {
    const colors: Record<string, string> = {
      education: 'bg-blue-100 text-blue-700',
      health: 'bg-red-100 text-red-700',
      livelihood: 'bg-green-100 text-green-700',
      agriculture: 'bg-yellow-100 text-yellow-700',
      nutrition: 'bg-orange-100 text-orange-700',
      water_sanitation: 'bg-cyan-100 text-cyan-700',
      housing: 'bg-purple-100 text-purple-700',
      skills_training: 'bg-indigo-100 text-indigo-700',
      women_empowerment: 'bg-pink-100 text-pink-700',
      child_welfare: 'bg-rose-100 text-rose-700',
      other: 'bg-gray-100 text-gray-700',
    };
    return colors[sector] || colors.other;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="text-gray-500">Loading templates...</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Questionnaires</h1>
          <p className="text-gray-500 mt-1">Manage survey templates and questions</p>
        </div>
        <Button onClick={() => {
          resetForm();
          setShowCreateModal(true);
        }}>
          <Plus className="w-4 h-4" /> New Template
        </Button>
      </div>

      {/* Templates Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {templates.map((template) => (
          <Card key={template.id} className="p-6 hover:shadow-lg transition-shadow">
            <div className="flex items-start gap-4">
              <div className="w-12 h-12 bg-emerald-100 rounded-lg flex items-center justify-center flex-shrink-0">
                <ClipboardList className="w-6 h-6 text-emerald-600" />
              </div>
              <div className="flex-1 min-w-0">
                <div className="flex items-start justify-between gap-2">
                  <h3 className="font-semibold text-gray-900 truncate">{template.title}</h3>
                  {template.is_default && (
                    <span className="text-xs flex-shrink-0">
                      <Badge variant="info">Default</Badge>
                    </span>
                  )}
                </div>
                <p className="text-sm text-gray-600 mt-1">
                  <span className={`inline-block px-2 py-0.5 rounded text-xs font-medium ${getSectorColor(template.sector)}`}>
                    {template.sector_display || template.sector}
                  </span>
                </p>
                <p className="text-sm text-gray-500 mt-2 line-clamp-2">{template.description}</p>

                <div className="flex items-center gap-4 mt-4 text-xs text-gray-500">
                  <span>{template.sections_count || 0} sections</span>
                  <span>{template.questions_count || 0} questions</span>
                </div>

                <div className="flex items-center gap-2 mt-4 pt-4 border-t">
                  <button
                    onClick={() => {
                      setSelectedTemplate(template);
                      setShowViewModal(true);
                    }}
                    className="flex items-center gap-1 text-sm text-blue-600 hover:text-blue-800"
                  >
                    <Eye className="w-3.5 h-3.5" /> View
                  </button>
                  <button
                    onClick={() => handleEdit(template)}
                    className="flex items-center gap-1 text-sm text-emerald-600 hover:text-emerald-800"
                  >
                    <Edit className="w-3.5 h-3.5" /> Edit
                  </button>
                  <button
                    onClick={() => handleDuplicate(template)}
                    className="flex items-center gap-1 text-sm text-purple-600 hover:text-purple-800"
                  >
                    <Copy className="w-3.5 h-3.5" /> Duplicate
                  </button>
                  {!template.is_default && (
                    <button
                      onClick={() => handleDelete(template)}
                      className="flex items-center gap-1 text-sm text-red-600 hover:text-red-800 ml-auto"
                    >
                      <Trash2 className="w-3.5 h-3.5" /> Delete
                    </button>
                  )}
                </div>
              </div>
            </div>
          </Card>
        ))}

        {/* Create New Card */}
        <Card 
          className="p-6 border-2 border-dashed border-gray-300 hover:border-emerald-500 hover:bg-emerald-50 transition-all cursor-pointer flex items-center justify-center min-h-[280px]"
          onClick={() => {
            resetForm();
            setShowCreateModal(true);
          }}
        >
          <div className="text-center">
            <div className="w-12 h-12 bg-gray-100 rounded-full flex items-center justify-center mx-auto mb-3">
              <Plus className="w-6 h-6 text-gray-400" />
            </div>
            <p className="text-gray-600 font-medium">Create New Template</p>
            <p className="text-sm text-gray-500 mt-1">Start from scratch</p>
          </div>
        </Card>
      </div>

      {/* View Template Modal */}
      {selectedTemplate && showViewModal && (
        <Modal 
          isOpen={showViewModal} 
          onClose={() => {
            setShowViewModal(false);
            setSelectedTemplate(null);
          }} 
          title="Template Details"
        >
          <div className="space-y-4">
            <div>
              <label className="text-sm text-gray-500">Title</label>
              <p className="font-medium">{selectedTemplate.title}</p>
            </div>

            <div>
              <label className="text-sm text-gray-500">Description</label>
              <p className="text-gray-700">{selectedTemplate.description}</p>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-sm text-gray-500">Sector</label>
                <p className="font-medium">
                  <span className={`inline-block px-2 py-1 rounded text-sm ${getSectorColor(selectedTemplate.sector)}`}>
                    {selectedTemplate.sector_display || selectedTemplate.sector}
                  </span>
                </p>
              </div>
              <div>
                <label className="text-sm text-gray-500">Status</label>
                <p className="font-medium">
                  {selectedTemplate.is_default ? (
                    <Badge variant="info">Default Template</Badge>
                  ) : (
                    <Badge variant="default">Custom Template</Badge>
                  )}
                </p>
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-sm text-gray-500">Sections</label>
                <p className="font-medium">{selectedTemplate.sections_count || 0}</p>
              </div>
              <div>
                <label className="text-sm text-gray-500">Questions</label>
                <p className="font-medium">{selectedTemplate.questions_count || 0}</p>
              </div>
            </div>

            <div className="flex gap-3 pt-4 border-t">
              <Button variant="secondary" onClick={() => {
                setShowViewModal(false);
                setSelectedTemplate(null);
              }}>
                Close
              </Button>
              <Button onClick={() => {
                setShowViewModal(false);
                handleEdit(selectedTemplate);
              }}>
                <Edit className="w-4 h-4" /> Edit Template
              </Button>
            </div>
          </div>
        </Modal>
      )}

      {/* Create/Edit Template Modal */}
      <Modal 
        isOpen={showCreateModal} 
        onClose={() => {
          setShowCreateModal(false);
          resetForm();
        }} 
        title={isEditMode ? "Edit Template" : "Create New Template"}
      >
        <form className="space-y-4" onSubmit={handleSubmit}>
          {error && (
            <div className="bg-red-50 text-red-600 p-3 rounded-lg text-sm">
              {error}
            </div>
          )}

          <Input 
            label="Template Title *" 
            name="title"
            value={formData.title}
            onChange={handleInputChange}
            placeholder="e.g., Education Scholarship Assessment"
            required 
          />

          <Textarea 
            label="Description *" 
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            placeholder="Brief description of the questionnaire purpose"
            required
            rows={3}
          />

          <Select 
            label="Sector *"
            name="sector"
            value={formData.sector}
            onChange={handleInputChange}
            options={sectorOptions}
          />

          <label className="flex items-center gap-2 cursor-pointer">
            <input
              type="checkbox"
              name="is_default"
              checked={formData.is_default}
              onChange={handleInputChange}
              className="w-4 h-4 text-emerald-600 border-gray-300 rounded focus:ring-emerald-500"
            />
            <span className="text-sm text-gray-700">Set as default template for this sector</span>
          </label>

          <div className="flex gap-3 pt-4 border-t">
            <Button 
              variant="secondary" 
              type="button" 
              onClick={() => {
                setShowCreateModal(false);
                resetForm();
              }}
              disabled={submitting}
            >
              Cancel
            </Button>
            <Button type="submit" disabled={submitting}>
              {submitting ? (isEditMode ? 'Updating...' : 'Creating...') : (isEditMode ? 'Update Template' : 'Create Template')}
            </Button>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default QuestionnairesPage;
