import React, { useState, useEffect } from 'react';
import { Plus, Search, Filter, Download, Edit } from 'lucide-react';
import api, { endpoints } from '../api';
import { Project, PaginatedResponse, ProjectStatus, ProjectSector } from '../types';
import { Card, Button, Input, Select, Modal, Badge, ProgressBar, DataTable } from '../components/ui';

const ProjectsPage: React.FC = () => {
  const [projects, setProjects] = useState<Project[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [showModal, setShowModal] = useState<boolean>(false);
  const [selectedProject, setSelectedProject] = useState<Project | null>(null);
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [submitting, setSubmitting] = useState<boolean>(false);
  const [error, setError] = useState<string>('');

  // Form state
  const [formData, setFormData] = useState({
    code: '',
    title: '',
    description: '',
    sector: 'education' as ProjectSector,
    total_beneficiaries: '',
    sample_percentage: '15',
    client: '',
  });

  useEffect(() => {
    loadProjects();
  }, []);

  const loadProjects = async () => {
    try {
      const data = await api.get<PaginatedResponse<Project> | Project[]>(endpoints.projects);
      setProjects(Array.isArray(data) ? data : data.results);
    } catch (error) {
      console.error('Error loading projects:', error);
    }
    setLoading(false);
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setSubmitting(true);
    setError('');

    try {
      const total = parseInt(formData.total_beneficiaries) || 0;
      const percentage = parseInt(formData.sample_percentage) || 15;
      const sample_size = Math.ceil((total * percentage) / 100);

      const payload = {
        code: formData.code,
        title: formData.title,
        description: formData.description,
        sector: formData.sector,
        status: 'draft' as ProjectStatus,
        total_beneficiaries: total,
        sample_size: sample_size,
        sample_percentage: percentage,
        client: formData.client || null,
      };

      await api.post(endpoints.projects, payload);

      // Reset form and close modal
      setFormData({
        code: '',
        title: '',
        description: '',
        sector: 'education',
        total_beneficiaries: '',
        sample_percentage: '15',
        client: '',
      });
      setShowModal(false);

      // Reload projects
      loadProjects();
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to create project');
    }
    setSubmitting(false);
  };

  const getStatusBadge = (status: ProjectStatus): React.ReactNode => {
    const variants: Record<ProjectStatus, 'default' | 'success' | 'warning' | 'danger' | 'info'> = {
      draft: 'default',
      planning: 'info',
      in_progress: 'warning',
      data_collection: 'info',
      analysis: 'info',
      reporting: 'info',
      completed: 'success',
      on_hold: 'danger'
    };
    return <Badge variant={variants[status]}>{status.replace('_', ' ')}</Badge>;
  };

  const columns = [
    { key: 'code' as const, label: 'Code' },
    { key: 'title' as const, label: 'Project Title' },
    { key: 'client_name' as const, label: 'Client' },
    { key: 'sector_display' as const, label: 'Sector' },
    { 
      key: 'status' as const, 
      label: 'Status',
      render: (val: unknown) => getStatusBadge(val as ProjectStatus)
    },
    { 
      key: 'completion_percentage' as const, 
      label: 'Progress',
      render: (val: unknown) => (
        <div className="w-24">
          <ProgressBar value={val as number} max={100} />
          <span className="text-xs text-gray-500">{`${val}%`}</span>
        </div>
      )
    },
    { key: 'sample_size' as const, label: 'Sample Size' },
  ];

  const filteredProjects = projects.filter(p => 
    p.title?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    p.code?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const sectorOptions = [
    { value: '', label: 'Select Sector' },
    { value: 'education', label: 'Education' },
    { value: 'health', label: 'Healthcare' },
    { value: 'livelihood', label: 'Livelihood' },
    { value: 'agriculture', label: 'Agriculture' },
    { value: 'women_empowerment', label: 'Women Empowerment' },
    { value: 'skill_development', label: 'Skill Development' },
    { value: 'microfinance', label: 'Microfinance' },
    { value: 'housing', label: 'Housing' },
    { value: 'water_sanitation', label: 'Water & Sanitation' },
  ];

  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold text-gray-900">Projects</h1>
          <p className="text-gray-500 mt-1">Manage impact assessment projects</p>
        </div>
        <Button onClick={() => setShowModal(true)}>
          <Plus className="w-4 h-4" /> New Project
        </Button>
      </div>

      <Card className="p-4">
        <div className="flex flex-wrap items-center gap-4 mb-4">
          <div className="relative flex-1 min-w-[200px] max-w-md">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-gray-400" />
            <input
              type="text"
              placeholder="Search projects..."
              className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-emerald-500"
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
          </div>
          <Button variant="outline">
            <Filter className="w-4 h-4" /> Filter
          </Button>
          <Button variant="outline">
            <Download className="w-4 h-4" /> Export
          </Button>
        </div>

        <DataTable 
          columns={columns} 
          data={filteredProjects}
          onRowClick={(row: Project) => setSelectedProject(row)}
          loading={loading}
        />
      </Card>

      {/* Project Detail Modal */}
      <Modal 
        isOpen={!!selectedProject} 
        onClose={() => setSelectedProject(null)}
        title={selectedProject?.title || 'Project Details'}
      >
        {selectedProject && (
          <div className="space-y-6">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-sm text-gray-500">Project Code</label>
                <p className="font-medium">{selectedProject.code}</p>
              </div>
              <div>
                <label className="text-sm text-gray-500">Client</label>
                <p className="font-medium">{selectedProject.client_name}</p>
              </div>
              <div>
                <label className="text-sm text-gray-500">Sector</label>
                <p className="font-medium">{selectedProject.sector_display}</p>
              </div>
              <div>
                <label className="text-sm text-gray-500">Status</label>
                <p>{getStatusBadge(selectedProject.status)}</p>
              </div>
              <div>
                <label className="text-sm text-gray-500">Total Beneficiaries</label>
                <p className="font-medium">{selectedProject.total_beneficiaries}</p>
              </div>
              <div>
                <label className="text-sm text-gray-500">Sample Size</label>
                <p className="font-medium">{selectedProject.sample_size}</p>
              </div>
            </div>

            <div>
              <label className="text-sm text-gray-500">Progress</label>
              <div className="mt-2">
                <ProgressBar value={selectedProject.completion_percentage} max={100} />
                <p className="text-sm text-gray-600 mt-1">
                  {selectedProject.completion_percentage}% completed
                </p>
              </div>
            </div>

            {selectedProject.description && (
              <div>
                <label className="text-sm text-gray-500">Description</label>
                <p className="text-gray-700 mt-1">{selectedProject.description}</p>
              </div>
            )}

            <div className="flex gap-3 pt-4 border-t">
              <Button variant="secondary" onClick={() => setSelectedProject(null)}>
                Close
              </Button>
              <Button>
                <Edit className="w-4 h-4" /> Edit Project
              </Button>
            </div>
          </div>
        )}
      </Modal>

      {/* Create Project Modal */}
      <Modal isOpen={showModal} onClose={() => setShowModal(false)} title="Create New Project">
        <form className="space-y-4" onSubmit={handleSubmit}>
          {error && (
            <div className="bg-red-50 text-red-600 p-3 rounded-lg text-sm">
              {error}
            </div>
          )}

          <div className="grid grid-cols-2 gap-4">
            <Input 
              label="Project Code" 
              name="code"
              value={formData.code}
              onChange={handleInputChange}
              placeholder="e.g., EDU-2025-001" 
              required 
            />
            <Input 
              label="Title" 
              name="title"
              value={formData.title}
              onChange={handleInputChange}
              placeholder="Project title" 
              required 
            />
          </div>

          <Select 
            label="Sector"
            name="sector"
            value={formData.sector}
            onChange={handleInputChange}
            options={sectorOptions}
          />

          <div className="grid grid-cols-2 gap-4">
            <Input 
              label="Total Beneficiaries" 
              name="total_beneficiaries"
              type="number" 
              value={formData.total_beneficiaries}
              onChange={handleInputChange}
              placeholder="50" 
              required
            />
            <Input 
              label="Sample Size (%)" 
              name="sample_percentage"
              type="number" 
              value={formData.sample_percentage}
              onChange={handleInputChange}
              placeholder="15" 
              min="10"
              max="100"
              required
            />
          </div>

          <div className="flex gap-3 pt-4">
            <Button 
              variant="secondary" 
              type="button" 
              onClick={() => setShowModal(false)}
              disabled={submitting}
            >
              Cancel
            </Button>
            <Button type="submit" disabled={submitting}>
              {submitting ? 'Creating...' : 'Create Project'}
            </Button>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default ProjectsPage;
