import React, { useState } from 'react';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import { MainLayout } from './components/layout/MainLayout';
import { LoadingSpinner } from './components/ui';
import {
  Dashboard,
  LoginPage,
  ProjectsPage,
  ResourcesPage,
  QuestionnairesPage,
  InterviewsPage,
  AnalyticsPage,
  SettingsPage,
} from './pages';
import { BrowserRouter } from 'react-router-dom';

type PageId = 'dashboard' | 'projects' | 'resources' | 'questionnaires' | 'interviews' | 'analytics' | 'settings';

const AppContent: React.FC = () => {
  const { user, loading } = useAuth();
  const [activePage, setActivePage] = useState<PageId>('dashboard');

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <LoadingSpinner size="lg" />
      </div>
    );
  }

  if (!user) {
    return <LoginPage />;
  }

  const renderPage = (): React.ReactNode => {
    switch (activePage) {
      case 'dashboard':
        return <Dashboard />;
      case 'projects':
        return <ProjectsPage />;
      case 'resources':
        return <ResourcesPage />;
      case 'questionnaires':
        return <QuestionnairesPage />;
      case 'interviews':
        return <InterviewsPage />;
      case 'analytics':
        return <AnalyticsPage />;
      case 'settings':
        return <SettingsPage />;
      default:
        return <Dashboard />;
    }
  };

  return (
    <MainLayout activePage={activePage} setActivePage={(page) => setActivePage(page as PageId)}>
      {renderPage()}
    </MainLayout>
  );
};

const App: React.FC = () => {
  return (
    <BrowserRouter>
    <AuthProvider>
      <AppContent />
    </AuthProvider>
    </BrowserRouter>
  );
};

export default App;
